import{ createSlice } from '@reduxjs/toolkit'

const initialCounterState = {fetchedMeals: {data: {}, error: null, isLoading: false}, selectedMeal: {data: {}, error: null, isLoading: false}, bookmarkedRecipes: []};

const mealsSlice = createSlice({
    name: 'meals',
    initialState: initialCounterState,
    reducers: {
        addfetchedMeals(state, action){
            state.fetchedMeals = action.payload;
        },
        addSelectedMeal(state, action){
            state.selectedMeal = action.payload;
            console.log(state.selectedMeal);
        },
        bookmarkMeal(state, action){
            state.bookmarkedRecipes.push(action.payload);
            console.log(state.bookmarkedRecipes)
        }
    }
});

export const mealsActions = mealsSlice.actions;
export const mealsReducer = mealsSlice.reducer;
