import classes from './Header.module.css';
import img1 from '../images/forky.png';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPenToSquare, faBookmark } from '@fortawesome/free-solid-svg-icons'
import Searcher from './HeaderComponents/Searcher';
import BookmarkModal from './UI/BookmarkModal';
import { useState, useRef } from 'react';

const recipe = <FontAwesomeIcon className={`${classes['icon-color']} ${classes.recipe}`} icon={faPenToSquare} />
const bookmark = <FontAwesomeIcon className={`${classes['icon-color']} ${classes.bookmarks}`} icon={faBookmark} />

function Header(){
    const [isShowBookmarks, setIsShowBookmarks] = useState(false);

    const showBookmarks = () => {
        setIsShowBookmarks(true);
        console.log('showww')
    }

    const closeBookmarks = () => {
        setIsShowBookmarks(false);
    }

    return (
        <div className={classes.header}>
            <img className={classes.image} src={img1} alt='forky'></img>
            <Searcher/>
            <div className={classes['recipe-book-container']}>
                <h1 className={classes.recipe}>{recipe} ADD RECIPE</h1>
                {isShowBookmarks ? 
                    <div>
                        <h1 onClick={showBookmarks} className={classes.bookmarks}>{bookmark} BOOKMARKS</h1>
                    </div> :
                    <h1 ref={bookmarkButtonRef} onClick={showBookmarks} className={classes.bookmarks}>{bookmark} BOOKMARKS</h1>
                }
            </div>
            {isShowBookmarks ? <BookmarkModal bookmarkButtonRef={bookmarkButtonRef} closeBookmarks={closeBookmarks}/> : ''}
        </div>
    );
}

export default Header;
