import React, { useRef, useState } from 'react';
import { useDispatch } from 'react-redux';
import { Button, Tour } from 'antd';
import { useSelector } from 'react-redux';

const PageTour = () => {
  const ref = useRef(null);
  const searcher = useRef(null);

  console.log(searcher)
  const [open, setOpen] = useState(false);
  const { refsTutorial } = useSelector(state => state.meals);
  console.log(refsTutorial)
  const dispatch = useDispatch(null);

          dispatch(mealsActions.addRefsToTutorial({searcher: searcherRef.current}));

  const steps = [
    {
      title: 'Center',
      description: 'Displayed in the center of screen.',
      target: null,
    },
    {
      title: 'Right',
      description: 'On the right of target.',
      placement: 'right',
      target: () => refsTutorial.searcher,
    },
    {
      title: 'Top',
      description: 'On the top of target.',
      placement: 'top',
      target: () => ref.current
    },
  ];

  const tryOnChange = (pageNumber) => {
    console.log('onchange', pageNumber);
  }

  return (
    <>
      <Button type="primary" onClick={() => setOpen(true)} ref={ref}>
        Begin Tour
      </Button>

      <Tour onChange={tryOnChange} open={open} onClose={() => setOpen(false)} steps={steps} />
    </>
  );
};
export default PageTour;
