import classes from './MealList.module.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowRight } from '@fortawesome/free-solid-svg-icons'
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons'
import { useSelector } from 'react-redux'
import { useEffect } from 'react';

const rightArrow = <FontAwesomeIcon icon={faArrowRight} />
const leftArrow = <FontAwesomeIcon icon={faArrowLeft} />

function MealList(){
    const { fetchedMeals } = useSelector(state => state.meals);
    const renderRange = useSelector([0, 4]);

    console.log(fetchedMeals)
    const numberOfRecipesFetched = Object.keys(fetchedMeals.data).length;

    
    return (
        <div className={classes['meal-list']}>
            <div>
                {numberOfRecipesFetched > 1 ? fetchedMeals.data.data.recipes.map((recipe, i) => {
                    return (
                        <div key={i} className={classes['meal-container']}>
                            <img className={classes.img} src={recipe['image_url']} alt={recipe.title}></img>
                            <div className={classes['text-container']}>
                                <h1 className={classes.title}>{recipe.title.toUpperCase()}</h1>
                                <p className={classes.description}>{recipe.publisher}</p>
                            </div>
                        </div>
                    );
                }) : ''}
            </div>
            <div className={classes['buttons-container']}>
                <button className={classes.button}>{leftArrow} Page 1</button>
                <button className={classes.button}>Page 2 {rightArrow}</button>
            </div>
        </div>
    );
}

export default MealList;
