import classes from './AddRecipeModal.module.css';
import useHttp from '../../hooks/use-http';
import { useDispatch } from 'react-redux';
import { mealsActions } from '../../redux/meals';

function AddRecipeModal(){
    const {isLoading, error, sendRequest} = useHttp();
    const dispatch = useDispatch();

    const pruebaPost = {publisher:"Closet Cooking",image_url:"http://forkify-api.herokuapp.com/images/BBQChickenPizzawithCauliflowerCrust5004699695624ce.jpg",title:"Cauliflower Pizza Crust (with BBQ Chicken Pizza)",id:"5ed6604591c37cdc054bcd09"};

    const handleUploadRecipe = (recipeId) => {
        sendRequest(
            {
                url: `https://forkify-api.herokuapp.com/api/v2/recipes&?key=4921d73e-5fc2-47b6-90a8-dfe1ae39f7cc`,
                method: 'POST',
                body: pruebaPost
            }, 
            (data) => dispatch(
                mealsActions.addSelectedMeal({data: data, error: error, isLoading: isLoading})
        ));
    }

    return (
        <div onClick={handleUploadRecipe} className={classes['add-recipe-container']}>
            <button>UPLOAD</button>
        </div>
    );
}

export default AddRecipeModal;
