import{ createSlice } from '@reduxjs/toolkit'
import { useRef } from 'react';

// const searcherRef = useRef(null);

// const initialRefState = {refsTutorial: {searcher: searcherRef}};

const refsToTutorialSlice = createSlice({
    name: 'refsTutorial',
    initialState: initialRefState,
    reducers: {
        addRefsToTutorial(state, action){
            state.refsTutorial = {...state.refsTutorial, ...action.payload};
        }
    }
});

export const refsToTutorialActions = refsToTutorialSlice.actions;
export const refsToTutorialReducer = refsToTutorialSlice.reducer;
