import classes from './BookmarkModal.module.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowRight, faTriangleExclamation } from '@fortawesome/free-solid-svg-icons'
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons'
import { useSelector, useDispatch } from 'react-redux'
import { mealsActions } from '../../redux/meals';
import { useEffect, useRef, useState } from 'react';
import useHttp from '../../hooks/use-http';

const rightArrow = <FontAwesomeIcon icon={faArrowRight} />
const leftArrow = <FontAwesomeIcon icon={faArrowLeft} />
const triangleExclamation = <FontAwesomeIcon className={`${classes.icon}`} icon={faTriangleExclamation} />

function BookmarkModal(props){
    const { bookmarkedRecipes } = useSelector(state => state.meals);
    const bookmarkListRef = useRef(null);

    // const handleClickOutside = (event) => {
    // if (bookmarkListRef.current && !bookmarkListRef.current.contains(event.target) && props.bookmarkButtonRef.current !== event.target) {
    //     props.closeBookmarks();
    //     console.log('document')
    // }};
    // useEffect(() => {

    //     document.addEventListener('mousedown', handleClickOutside);

    //     return () => {
    //         document.removeEventListener('mousedown', handleClickOutside);
    //     };
    // }, []);

    // const handleModalClick = (event) => {
    //     event.stopPropagation();
    // };
    
    // const [renderRange, setRenderRange] = useState([0, 4]);
    // const [pageNumber, setPageNumber] = useState(1);

    // useEffect(() => {
    //     setRenderRange([0, 4]);
    //     setPageNumber(1);

    // }, [fetchedMeals]);

    const {isLoading, error, sendRequest} = useHttp();
    const dispatch = useDispatch();

    const handleSelectMeal = (recipeId) => {
        sendRequest(
            {url: `https://forkify-api.herokuapp.com/api/v2/recipes/${recipeId}?key=4921d73e-5fc2-47b6-90a8-dfe1ae39f7cc`}, 
            (data) => dispatch(
                mealsActions.addSelectedMeal({data: data, error: error, isLoading: isLoading})
        ));
    }

    const isBookmarkedRecipes = bookmarkedRecipes.length > 0;
    let recipesToRender = [];

    if(isBookmarkedRecipes){
        recipesToRender = [];

        for (let i = 0; i < isBookmarkedRecipes.length; i++) {
            // if(i < renderRange[0] || i > renderRange[1]) continue;

            const recipe = isBookmarkedRecipes[i];
            recipesToRender.push(
                <div onClick={handleSelectMeal.bind(null, recipe.id)} key={recipe.id} id={recipe.id} className={classes['meal-container']}>
                    <img className={classes.img} src={recipe.image_url} alt={recipe.title}></img>
                    <div className={classes['text-container']}>
                        <h1 className={classes.title}>{recipe.title.length > 30 ? `${recipe.title.toUpperCase().slice(0, 30)}...` : recipe.title.toUpperCase()}</h1>
                        <p className={classes.description}>{recipe.publisher}</p>
                    </div>
                </div>
            );
        }
    }

    // const defaultText = {triangleExclamation} <p>d</p>);

    // const renderNextPage = () => {
    //     setRenderRange((lastVal) => ([lastVal[0] + 5, lastVal[1] + 5]));
    //     setPageNumber((lastVal) => (lastVal + 1));
    // }

    // const renderPreviousPage = () => {
    //     setRenderRange((lastVal) => ([lastVal[0] - 5, lastVal[1] - 5]));
    //     setPageNumber((lastVal) => (lastVal - 1));
    // }
    
    return (
        <div onClick={handleModalClick} ref={bookmarkListRef} className={classes['bookmark-list']}>
            {isBookmarkedRecipes ? {recipesToRender} : 
                <div className={classes['default-text-container']}>
                    <p className={classes['default-text']}>{triangleExclamation} No bookmarks yet. Find a nice recipe and bookmark it</p>
                </div>
            }
            {/* <div>
            </div> */}
            {/* <div className={classes['buttons-container']}>
                {pageNumber > 1 ?
                    <button onClick={renderPreviousPage} className={classes.button}>{leftArrow} Page {pageNumber - 1}</button>
                    : ''
                }
                <div></div>
                {recipesToRender.length !== 0 && 
                recipesToRender[recipesToRender.length - 1].props.id !== fetchedMeals.data.data.recipes[fetchedMeals.data.data.recipes.length - 1].id ?
                    <button onClick={renderNextPage} className={classes.button}>Page {pageNumber + 1} {rightArrow}</button>
                    : ''
                }
            </div> */}
        </div>
    );
}

export default BookmarkModal;
